users = CREATE TABLE `Gurukul`.`users` ( `id` INT NOT NULL AUTO_INCREMENT , `name` VARCHAR(100) NOT NULL , `email` VARCHAR(100) NOT NULL , `password` VARCHAR(255) NOT NULL , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;
notes = CREATE TABLE notes ( note_id INT AUTO_INCREMENT PRIMARY KEY, user_id INT NOT NULL, title VARCHAR(200) NOT NULL, content TEXT, created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP, updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP )
taska = CREATE TABLE tasks ( task_id INT AUTO_INCREMENT PRIMARY KEY, user_id INT NOT NULL, title VARCHAR(200) NOT NULL, description TEXT, due_date DATE, status ENUM('pending','completed','overdue') DEFAULT 'pending', progress INT DEFAULT 0, created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP )
schedule=CREATE TABLE schedule ( event_id INT AUTO_INCREMENT PRIMARY KEY, user_id INT NOT NULL, title VARCHAR(200) NOT NULL, description TEXT, event_date DATE NOT NULL, event_time TIME, location VARCHAR(200) )
chat_id = CREATE TABLE chat_history ( chat_id INT AUTO_INCREMENT PRIMARY KEY, user_id INT NOT NULL, role ENUM('user','ai') NOT NULL, message TEXT NOT NULL, created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP )
